import React, {Component, useEffect, useState} from "react";
import classes from './Day.module.scss';
import clock from './../../images/clock.png';
const Day = (props) => {
    const [isOpen, setIsOpen] = useState(false);
    // state = {
    //     isOpen: false,
    // };

    // render() {
        const {course} = props;
    useEffect( () => {
        if(course.date === course.date) {
            setIsOpen(true);
        }
        else {
            setIsOpen(false);
        }
    },[course.date])
        const descriptionHandler = () => {
            setIsOpen(!isOpen);
        }

        const dayDescription = isOpen && course.date === course.date && <div className={classes.Detail}>
            <h3>Уроки на {course.date} мая</h3>
            {course.event.map(({ title, name, time, duration}) => {
                return (
                        <div className={classes.Event}>
                            <span className={classes.Time}>{time}</span>
                            <span className={classes.Title}>{title}</span>
                            <span className={classes.Name}>{name}</span>
                            <span className={classes.Duration}><img className={classes.Clock} src={clock} alt=""/>{duration} минут</span>
                        </div>

                    )
            })}
        </div>
        return (
            <React.Fragment>
                <div className={classes.Day} onClick={descriptionHandler}>
                    <p>{course.date}</p>
                    <span>{course.event.length} Событий</span>
                </div>
                {dayDescription}
            </React.Fragment>

        )

    // }

}
export default Day;
