import React, {Component} from "react";
import classes from './Day.module.scss';

class Day extends Component {
    state = {
        isOpen: false,
    }
    render() {
        const {course} = this.props;
        const dayDescription = this.state.isOpen && <div className={classes.Detail}>
            <h3>Уроки на {course.date} мая</h3>
            {course.event.map(({ title, name, time}) => {
                return (
                        <div className={classes.Event}>
                            <span>{title}</span>
                            <span>{name}</span>
                            <span>{time} минут</span>
                        </div>

                    )
            })}
        </div>
        return (
            <React.Fragment>
                <div className={classes.Day} onClick={this.descriptionHandler}>
                    <p>{course.date}</p>
                    <span>{course.event.length} Событий</span>
                </div>
                {dayDescription}
            </React.Fragment>

        )

    }
    descriptionHandler = () => {
        this.setState(state => ({
            isOpen: !this.props.course.date ? !this.state.isOpen  : this.state.isOpen,
        }));
    }
}
export default Day;
