import React, {Component} from "react";

class Day extends Component {
    state = {
        isOpen: true,
    }
    render() {
        const {course} = this.props;
        const dayDescription = this.state.isOpen && <section>{course.event}</section>
        return (
            <div>
                <span>{course.event.title}</span>
            </div>
        )
    }

}
export default Day;
