import React, {Component} from "react";
import classes from './Day.module.scss';
import clock from './../../images/clock.png';

class Day extends Component {
    constructor(props) {
        super(props);

        this.state = {
            isOpen: false,
        };
        this.descriptionHandler = this.descriptionHandler.bind(this);


    }
    descriptionHandler() {
        this.setState(state => ({
            isOpen: !this.state.isOpen,
        }));
    }
    render() {
        const {course} = this.props;
        const dayDescription = this.state.isOpen && <div className={classes.Detail}>
            <h3>Уроки на {course.date} мая</h3>
            {course.event.map(({ title, name, time, duration}) => {
                return (
                        <div className={classes.Event}>
                            <span className={classes.Time}>{time}</span>
                            <span className={classes.Title}>{title}</span>
                            <span className={classes.Name}>{name}</span>
                            <span className={classes.Duration}><img className={classes.Clock} src={clock} alt=""/>{duration} минут</span>
                        </div>

                    )
            })}
        </div>
        return (
            <React.Fragment>
                <div className={classes.Day} onClick={this.descriptionHandler}>
                    <p>{course.date}</p>
                    <span>{course.event.length} Событий</span>
                </div>
                {dayDescription}
            </React.Fragment>

        )

    }

}
export default Day;
