import React from "react";
import Day from "../Day/Day";
import classes from './Month.module.scss';
const Month = ({courses}) => {
    const weekdays = ["Пн", "Вт", "Ср", "Чт", "Пт", "Сб", "Вс"];
    const date = courses.date;
    const dayElement = courses.map( course =>
        <div key={course.date}><Day course={course} selected={date === course.date} hovering={date !== course.date} onMouseEnter={handleMouseEnter}
                                    onMouseLeave={handleMouseLeave}/></div>
    )
    return (
        <div>
            <div className={classes.Weekday}>{weekdays.map((weekday) => weekday)}</div>
            <div className={classes.Month}>
                {dayElement}
            </div>
        </div>


    )

const handleMouseEnter =(date) =>{
    this.setState({
        isOpen: date,
    });
}

const handleMouseLeave = () => {
    this.setState({
        isOpen: null,
    });
}
}
export default Month;
