import React, {Component} from "react";

class Day extends Component {
    state = {
        isOpen: true,
    }
    render() {
        const {course} = this.props;
        const dayDescription = this.state.isOpen && <section>{course.event}</section>
        return (
            <div>
                <p>{course.date}</p>
                <span>{course.event.title}</span>
                <span>{course.event.name}</span>
                <span>`${course.event.time} + "минут"`</span>
            </div>
        )
    }

}
export default Day;
