import React, {Component} from "react";
import classes from './Day.module.scss';

class Day extends Component {
    state = {
        isOpen: false,
    }
    render() {
        const {course} = this.props;
        const dayDescription = this.state.isOpen && <section>
            {course.event.map(({ title, name, time}) => {
                return (
                        <div className={classes.Event}>
                            <span>{title}</span>
                            <span>{name}</span>
                            <span>{time} минут</span>
                        </div>
                    )
            })}

        </section>
        return (
            <div className={classes.Day} onClick={this.descriptionHandler}>
                <p>{course.date}</p>
                <span>{course.event.length} Событий</span>
                <div className={classes.Detail}>{dayDescription}</div>
            </div>
        )

    }
    descriptionHandler = () => {
        this.setState({
            isOpen: !this.state.isOpen,
        })
    }
}
export default Day;
