import React, {Component} from "react";
import Day from "../Day/Day";
import classes from './Month.module.scss';
class Month extends Component {
    constructor(props) {
        super(props);

        this.renderWeek = this.renderWeek.bind(this);
        this.handleMouseEnter = this.handleMouseEnter.bind(this);
        this.handleMouseLeave = this.handleMouseLeave.bind(this);

        this.state = {
            hoveredDate: null,
            events: null
        };
    }
    render() {
        const months = [
            "Январь",
            "Февраль",
            "Март",
            "Апрель",
            "Май",
            "Июнь",
            "Июль",
            "Август",
            "Сентябрь",
            "Остябрь",
            "Ноябрь",
            "Декабрь"
        ];

        function getMonthName(index) {
            return months[index];
        }

        const weekdays = [
            "Вс",
            "Пн",
            "Вт",
            "Ср",
            "Чт",
            "Пт",
            "Сб"
        ];
        function abbreviationForWeekday(weekday) {
            return weekday.substring(0, 2);
        }

        const WEEK_LENGTH = 7;
        function getWeeksForMonth(month, year) {
            const firstOfMonth = new Date(year, month, 1);
            const firstDayOfWeek = firstOfMonth.getDay();
            const weeks = [[]];

            let currentWeek = weeks[0];
            let currentDate = firstOfMonth;

            for (let i = 0; i < firstDayOfWeek; i++) {
                currentWeek.push(null);
            }

            while (currentDate.getMonth() === month) {
                if (currentWeek.length === WEEK_LENGTH) {
                    currentWeek = [];
                    weeks.push(currentWeek);
                }

                currentWeek.push(currentDate);
                currentDate = new Date(year, month, currentDate.getDate() + 1);
            }

            while (currentWeek.length < 7) {
                currentWeek.push(null);
            }

            return weeks;
        }
        const {month, year } = this.props;
        const week = getWeeksForMonth(month, year);
        // const weekdays = ["Пн", "Вт", "Ср", "Чт", "Пт", "Сб", "Вс"];
        const weeksMarkup = week.map((week, index) => {
            return (
                <div role="row" className="Week" key={index}>
                    {week.map(this.renderWeek)}
                </div>
            );
        });
        const weekDaysMarkup = weekdays.map(weekday => {
            return (
                <Weekday
                    key={weekday}
                    title={abbreviationForWeekday(weekday)}
                    current={true}
                    label={weekday}
                />
            );
        });
        const dayElement = courses.map( course =>
            <div key={course.date}><Day course={course} /></div>
        )
        const weekdayElement = weekdays.map( day =>
            <div className={classes.Item}><p>{day}</p></div>
        )
        return (
            <div>
                <div className={classes.Weekday}>{weekdayElement}</div>
                <div className={classes.Month}>
                    {dayElement}
                    {weekDaysMarkup}
                    {weeksMarkup}
                </div>
            </div>
        )
    }

}
export default Month;
