import React, {Component} from "react";
import Day from "../Day/Day";
import classes from './Month.module.scss';
class Month extends Component {
    state = {
        isOpen: false,
    }
    render() {
        const {course} = this.props;
        const dayDescription = this.state.isOpen && <div className={classes.Detail}>
            <h3>Уроки на {course.date} мая</h3>
            {course.event.map(({title, name, time}) => {
                return (
                    <div className={classes.Event}>
                        <span>{title}</span>
                        <span>{name}</span>
                        <span>{time} минут</span>
                    </div>

                )
            })}
        </div>
        const dayElement = courses.map( course =>
            <div key={course.date}><Day course={course}/></div>
        )
        return (
            <div className={classes.Month}>
                {dayElement}
            </div>

        )
    }

}
export default Month;
