import React, {Component} from "react";
import classes from './Day.module.scss';

class Day extends Component {
    state = {
        isOpen: false,
    }
    render() {
        const {course} = this.props;
        const dayDescription = this.state.isOpen && <section>
            <span>{course.event.title}</span>
            <span>{course.event.name}</span>
            <span>{course.event.time} минут</span>
        </section>
        return (
            <div className={classes.Day} onClick={descriptionHandler}>
                <p>{course.date}</p>
                <span>{course.event.length} Событий</span>
                {dayDescription}
            </div>
        )
        const descriptionHandler = () => {
            this.setState({
                isOpen: !this.state.isOpen,
            })
        }
    }

}
export default Day;
